import "./Nav.css";
import { Link, useMatch, useResolvedPath, useNavigate } from "react-router-dom";

import Burger from "../Burger-button/Burger";
const Navigation_bar = () => {
  return (
    <header>
      <div className="mobile-top-bar"></div>
      <div className="logo">
        <img src="./1.png" alt="marche pas" />
        <h1>
          Dev<br></br> Academy
        </h1>
      </div>
      <Burger></Burger>

      <nav className="nav" id="nav">
        <CustomLink to="/" onClick={() => enable_scroll()}>
          Home
        </CustomLink>
        <CustomLink to="/Free-Tutorials" onClick={() => enable_scroll()}>
          Tutorials
        </CustomLink>
        <CustomLink to="/Premium-courses" onClick={() => disable_scroll()}>
          Courses
        </CustomLink>
      </nav>
    </header>
  );
};

function CustomLink({ to, children, ...props }) {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname, end: true });

  return (
    <li className={isActive ? "active" : ""}>
      <Link to={to} {...props}>
        {children}
      </Link>
    </li>
  );
}

export default Navigation_bar;
